"https://quickchart.io/chart?c=" & EncodeUrl(
    "{
    type: 'line',
    data: {
      labels: [" & Concat(
        LogiSam_LineChart.Data,
        "'" & Label & "'",
        ","
    ) & "],
      datasets: [
        {
          label: '" & LogiSam_LineChart.DataSetName & "',
          backgroundColor: '"& LogiSam_LineChart.BackgroundColor&"',
          borderColor: '"&LogiSam_LineChart.BorderColor&"',
          borderWidth: 1,
          data: [" & Concat(
        LogiSam_LineChart.Data,
        Data ,
        ","
    ) & "], fill: "&If(LogiSam_LineChart.LineFill,"true","false")&" ,
        }
      ],
    },
    options: {
      title: {
        display: true,
        text: '" & LogiSam_LineChart.ChartTitle & "',
      },
      plugins: {
        datalabels: {
          anchor: 'center',
          align: 'center',
          color: '#666',
          font: {
            weight: 'normal',
          },
        },

      roundedBars: "&If(LogiSam_LineChart.RoundedBorders,"true","false")&" 

      },
    },
  }"
)

